-- Create "admin_areas" table
CREATE TABLE `admin_areas` (`id` int unsigned NOT NULL AUTO_INCREMENT, `deleted_at` timestamp NULL, `name` varchar(255) NOT NULL COMMENT "Administrative area name", `abbr` varchar(255) NULL COMMENT "Administrative area abbreviation, CSV values", `memo` longtext NULL COMMENT "Remarks", `created_at` timestamp NULL, `updated_at` timestamp NULL, `lft` bigint NULL, `rgt` bigint NULL, `parent_id` int unsigned NULL, PRIMARY KEY (`id`), INDEX `admin_areas_admin_areas_children` (`parent_id`), CONSTRAINT `admin_areas_admin_areas_children` FOREIGN KEY (`parent_id`) REFERENCES `admin_areas` (`id`) ON UPDATE NO ACTION ON DELETE RESTRICT) CHARSET utf8mb4 COLLATE utf8mb4_bin COMMENT "Administrative area table";
